mapper.insert:
INSERT INTO ABILITIES (
	ABILITY_NAME 
	,ABILITY_SEQ
	,SUB_CATEGORY_SEQ
	,VISIBLE
	)
	values(
	#{ABILITY_NAME}
	,#{ABILITY_SEQ}
	,#{SUB_CATEGORY_SEQ}
	,'T'
	)
	ON DUPLICATE KEY UPDATE 
	ABILITY_NAME = #{ABILITY_NAME}
	,ABILITY_SEQ = #{ABILITY_SEQ}
	,SUB_CATEGORY_SEQ = #{SUB_CATEGORY_SEQ}
	,VISIBLE = 'T'

mapper.update:
update ABILITIES
set VISIBLE = 'F'
where ABILITY_SEQ = #{ABILITY_SEQ}
