mapper.select:
select SUB_CATEGORY_SEQ ,SUB_CATEGORY_NAME ,CATEGORY_SEQ from SUB_CATEGORY
and CATEGORY_SEQ = #{CATEGORY_SEQ}
and VISIBLE = 'T'

mapper.insert:
INSERT INTO EQUIP(
	EQUIP_SEQ
	,SUB_CATEGORY_SEQ
	,MANUFACTURER
	,DESCRIPTION
	,MANAGER
	,EQUIP_NAME
	,VISIBLE
	,AVAILABLE
	)
values(
	#{EQUIP_SEQ}
	,#{SUB_CATEGORY_SEQ}
	,#{MANUFACTURER}
	,#{DESCRIPTION}
	,#{MANAGER}
	,#{EQUIP_NAME}
	,'T'
	,#{AVAILABLE}
	)
ON DUPLICATE KEY UPDATE
EQUIP_SEQ = #{EQUIP_SEQ}
,SUB_CATEGORY_SEQ = #{SUB_CATEGORY_SEQ}
,MANUFACTURER = #{MANUFACTURER}
,DESCRIPTION = #{DESCRIPTION}
,MANAGER = #{MANAGER}
,EQUIP_NAME = #{EQUIP_NAME}
,VISIBLE = 'T'
,AVAILABLE = #{AVAILABLE}

mapper.update:
update EQUIP
set VISIBLE = 'F'
where EQUIP_SEQ = #{EQUIP_SEQ}
