mapper.insert:
insert into member(
MEMBER_SEQ, ID, PASSWORD, TEL,
NAME, SUB_ADDR_SEQ, AUTHORITY_SEQ, SIGNUP_DATE, VISIBLE
)value(#{MEMBER_SEQ},#{ID},#{PASSWORD},#{TEL},#{NAME},#{cate2},'1',CURRENT_DATE(
),'TRUE')

mapper.update:
update member set ID=#{ID}, PASSWORD=#{PASSWORD},
NAME=#{NAME} where ID =
#{ID}

update member set ID=#{ID}, PASSWORD=#{PASSWORD},
NAME=#{NAME}, TEL=#{TEL}, SUB_ADDR_SEQ=#{cate2} where MEMBER_SEQ =
#{MEMBER_SEQ}

mapper.select:
select
ABILITY_SEQ
from member_ability_rel
where MEMBER_SEQ=#{member_seq}
and
ABILITY_SEQ=#{ability_seq}
