mapper.select:
select ORGANIZATION_SEQ , NAME , TELEPHONE , ORDER_NUMBER , PARENT_ORGANIZATION_SEQ from CIP_ORGANIZATION
and PARENT_ORGANIZATION_SEQ = #{PARENT_ORGANIZATION_SEQ}
and PARENT_ORGANIZATION_SEQ is null
and COMMON_CODE_ID = #{COMMON_CODE_ID}

mapper.insert:
MERGE INTO CIP_ORGANIZATION CIP1
     USING (SELECT #{ORGANIZATION_SEQ} AS ORGANIZATION_SEQ FROM DUAL) CIP2
        ON (CIP1.ORGANIZATION_SEQ = CIP2.ORGANIZATION_SEQ)
 WHEN MATCHED THEN
    UPDATE SET
		NAME = #{NAME} 	
		, TELEPHONE = #{TELEPHONE} 	
		, ORDER_NUMBER = #{ORDER_NUMBER} 	
		, PARENT_ORGANIZATION_SEQ = #{PARENT_ORGANIZATION_SEQ} 	
 WHEN NOT MATCHED THEN
    INSERT (
		ORGANIZATION_SEQ	
		, NAME	
		, TELEPHONE	
		, ORDER_NUMBER	
		, PARENT_ORGANIZATION_SEQ	
	)
	values(
		#{ORGANIZATION_SEQ}	
		, #{NAME}	
		, #{TELEPHONE}	
		, #{ORDER_NUMBER}	
		, #{PARENT_ORGANIZATION_SEQ}	
	)
